import React, { useState } from 'react';
import "./Page2.css";

export default function Page2() {
  const [isPolicyVisible, setIsPolicyVisible] = useState(false);

  const handlePolicyClick = () => {
    setIsPolicyVisible(true);
  };

  const handleNextClick = () => {
    // Handle navigation to the next screen
  };

  return (
    <div className="container">
      <div className="header">
        <div className="logo">Your logo</div>
        <div className="title">Cyber Safety</div>
        <div className="subtitle">Introduction to cyber safety</div>
        <div className="nav">
          <button className="menu-button">Menu</button>
          <button className="mute-button">Mute</button>
          <button className="exit-button">Exit</button>
        </div>
      </div>
      <div className="content">
        <div className="text">
          <p>
            Our Policy sets out responsibilities and standards to help
            keep our data safe.
          </p>
          <p>
            If you are not yet familiar with it, select the Policy image to
            take a look, otherwise select Next to continue.
          </p>
        </div>
        <div className="policy">
          {isPolicyVisible ? (
            <div className="policy-content">
              {/* Your Policy content here */}
            </div>
          ) : (
            <div className="policy-image" onClick={handlePolicyClick}>
              <div className="policy-text">Your Policy here</div>
              <div className="magnifier">+</div>
            </div>
          )}
        </div>
      </div>
      <div className="footer">
        <button className="back-button">Back</button>
        <button className="next-button" onClick={handleNextClick}>
          Next
        </button>
      </div>
    </div>
  );
}
