import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Page2.css';

export default function MenuPage() {
  const navigate = useNavigate();

  const menuItems = [
    "Introduction to cyber safety",
    "Passwords",
    "Socially engineered attacks",
    "Data security risks",
    "Your responsibilities",
    "More information",
  ];

  const handleItemClick = (index) => {
    if (index === 0) {
      navigate('/video'); // Navigate to the VideoPage
    }
    // Handle other menu item clicks if needed
  };

  return (
    <div className="menu-container">
      <div className="menu-header">
        <div className="logo">Your logo</div>
        <div className="menu-title">Menu</div>
        <div className="menu-subtitle">Select the first topic to begin.</div>

      </div>
      <div className="menu-list">
        {menuItems.map((item, index) => (
          <div
            key={index}
            className={`menu-item ${index === 0 ? 'active' : ''}`}
            onClick={() => handleItemClick(index)} // Add onClick handler
          >
            <div className="menu-item-text">{index + 1}. {item}</div>
            <div className="menu-item-icon">âž”</div>
          </div>
        ))}
      </div>
    </div>
  );
}
